head.script:
//
(function() {
  var isBot = /googlebot|bingbot|yandex|baiduspider|duckduckbot|slurp|facebookexternalhit|twitterbot|rogerbot|linkedinbot|embedly|quora|showyoubot|outbrain|pinterest|applebot|semrushbot|ahrefsbot|mj12bot|dotbot|petalbot|bytespider|gptbot|claudebot|anthropic/i.test(navigator.userAgent);

  if (isBot) {
    return;
  }

  var adBlockDetected = false;

  function detectAdBlock() {
    var testAd = document.createElement('div');
    testAd.innerHTML = '&nbsp;';
    testAd.className = 'adsbox ad-placement ad-placeholder adbanner adsbygoogle adslot';
    testAd.style.position = 'absolute';
    testAd.style.left = '-9999px';
    testAd.style.height = '1px';
    document.body.appendChild(testAd);

    setTimeout(function() {
      if (testAd.offsetHeight === 0 || testAd.clientHeight === 0 || window.getComputedStyle(testAd).display === 'none') {
        adBlockDetected = true;
        showAdBlockModal();
      }
      document.body.removeChild(testAd);
    }, 100);
  }

  function showAdBlockModal() {
    var modal = document.getElementById('adblock-modal');
    if (!modal) {
      modal = document.createElement('div');
      modal.id = 'adblock-modal';
      modal.innerHTML = '<div class="adblock-content">' +
        '<div class="adblock-icon">üö´</div>' +
        '<h2 class="adblock-title">AdBlock Detected!</h2>' +
        '<p class="adblock-message">We noticed you\'re using an ad blocker. This site is supported by ads, and we need your support to keep providing great content for free.</p>' +
        '<div class="adblock-instructions">' +
        '<h3>üìã How to continue:</h3>' +
        '<ol>' +
        '<li>Disable your ad blocker for this site</li>' +
        '<li>Click the "I\'ve Disabled AdBlock" button below</li>' +
        '<li>Enjoy our content!</li>' +
        '</ol>' +
        '</div>' +
        '<button class="adblock-button" onclick="checkAdBlockAgain()">‚úÖ I\'ve Disabled AdBlock</button>' +
        '</div>';
      document.body.appendChild(modal);
    }
    modal.classList.add('show');
    document.body.classList.add('adblock-detected');
  }

  window.checkAdBlockAgain = function() {
    location.reload();
  };

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', detectAdBlock);
  } else {
    detectAdBlock();
  }
})();
