body.script:
//
(function() {
  // Skip for search engine bots to prevent crawling issues
  var isBot = /googlebot|bingbot|yandex|baiduspider|duckduckbot|slurp|facebookexternalhit|twitterbot|rogerbot|linkedinbot|embedly|quora|showyoubot|outbrain|pinterest|applebot|semrushbot|ahrefsbot|mj12bot|dotbot|petalbot|bytespider|gptbot|claudebot|anthropic/i.test(navigator.userAgent);

  if (isBot) {
    return;
  }

  // Run immediately, no waiting for DOM
  var verificationPages = [
    '/2025/11/building-your-first-gpt-powered.html',
    '/2025/11/how-to-make-money-with-telegram-bots.html',
    '/2025/11/how-to-download-youtube-videos-with.html',
    '/2025/11/how-to-set-theme.html',
    '/2025/11/how-to-download-instagram-reels-using.html'
  ];

  var timePerPage = 15; // 15 seconds

  function initTimer() {
    try {
      // Get URL parameters
      var urlParams = new URLSearchParams(window.location.search);
      var sessionId = urlParams.get('session');
      var appUrl = urlParams.get('app_url');

      // Only run if session ID exists
      if (!sessionId) {
        return;
      }

      var currentPath = window.location.pathname;
      var currentPageIndex = verificationPages.indexOf(currentPath);

      // Check if current page is part of verification
      if (currentPageIndex === -1) {
        window.location.href = verificationPages[0] + '?session=' + sessionId + (appUrl ? '&app_url=' + encodeURIComponent(appUrl) : '');
        return;
      }

      var currentPageNum = currentPageIndex + 1;
      var totalPages = verificationPages.length;

      // Get the timer element (already in HTML)
      var timerBar = document.getElementById('verification-timer');
      var timerContent = document.getElementById('timer-content');

      if (!timerBar || !timerContent) {
        console.error('Timer elements not found');
        return;
      }

      // Show the timer
      timerBar.style.display = 'block';

      // Create next page button container
      var nextButtonContainer = document.createElement('div');
      nextButtonContainer.id = 'next-page-container';
      nextButtonContainer.style.cssText = 'display: none; background: white; padding: 25px; border-radius: 12px; box-shadow: 0 4px 20px rgba(0,0,0,0.15); text-align: center; margin-top: 30px; border: 2px solid #667eea;';

      var postBody = document.querySelector('.post-body');
      if (postBody) {
        postBody.appendChild(nextButtonContainer);
      }

      var timeRemaining = timePerPage;

      function updateTimer() {
        var progress = ((currentPageNum / totalPages) * 100).toFixed(0);

        if (timeRemaining > 0) {
          timerContent.innerHTML = '<strong>üéØ Page ' + currentPageNum + ' of ' + totalPages + ' (' + progress + '%)</strong><br/>Please wait: ' + timeRemaining + ' seconds...';
          timeRemaining--;
          setTimeout(updateTimer, 1000);
        } else {
          timerContent.innerHTML = '<strong>‚úÖ Timer Complete!</strong> Scroll down to see the next page button';
          showNextPageButton();
        }
      }

      function showNextPageButton() {
        if (currentPageNum < totalPages) {
          var nextPageUrl = verificationPages[currentPageNum] + '?session=' + sessionId + (appUrl ? '&app_url=' + encodeURIComponent(appUrl) : '');
          nextButtonContainer.innerHTML = '<h3 style="color: #2196f3; margin-bottom: 10px;">‚úÖ Great Job!</h3>' +
            '<p style="color: #666; margin-bottom: 15px;">Page ' + currentPageNum + ' of ' + totalPages + ' complete</p>' +
            '<a href="' + nextPageUrl + '" onclick="return handleNextPageClick(event, this.href);" style="display: inline-block; padding: 15px 40px; background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; text-decoration: none; border-radius: 8px; font-size: 16px; font-weight: bold; box-shadow: 0 4px 12px rgba(102,126,234,0.4); transition: all 0.3s ease;">Go to Page ' + (currentPageNum + 1) + ' ‚Üí</a>';
          nextButtonContainer.style.display = 'block';
        } else {
          timerContent.innerHTML = 'üéâ <strong>ALL PAGES COMPLETE!</strong> Generating your code...';
          setTimeout(function() {
            if (appUrl) {
              window.location.href = appUrl + '/verify-ad?session=' + sessionId;
            } else {
              timerContent.innerHTML = '‚ùå Error: Missing app URL. Please contact support.';
            }
          }, 2000);
        }
      }

      // Next page click handler - opens popunder and navigates in one click
      // Make it globally accessible for onclick handlers
      window.handleNextPageClick = function(event, url) {
        event.preventDefault();

        try {
          // Open popunder ad in background (non-blocking)
          var popunder = window.open('https://www.effectivegatecpm.com/zn01rc1vt?key=78d0724d73f6154a582464c95c28210d', '_blank');
          if (popunder) {
            popunder.blur();
            window.focus();
          }
        } catch(e) {
          console.log('[Ad] Popunder blocked:', e);
        }

        // Navigate to next page immediately (no delay needed)
        window.location.href = url;

        return false;
      };

      // Start timer immediately
      updateTimer();

    } catch(e) {
      console.error('Timer init error:', e);
    }
  }

  // Run immediately
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initTimer);
  } else {
    initTimer();
  }
})();
