b:if.script:
//
(function() {
  'use strict';

  // Wait for DOM to be fully loaded
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initTableOfContents);
  } else {
    initTableOfContents();
  }

  function initTableOfContents() {
    try {
      // Find the article content container
      var articleContent = document.getElementById('article-content');
      if (!articleContent) {
        console.log('[TOC] Article content container not found');
        return;
      }

      // Find all H2 and H3 headings
      var headings = articleContent.querySelectorAll('h2, h3');

      // Only create TOC if there are 2 or more headings
      if (headings.length < 2) {
        console.log('[TOC] Not enough headings (' + headings.length + '), skipping TOC');
        return;
      }

      // Generate unique IDs for headings and build TOC structure
      var tocHTML = '';
      var headingIndex = 0;

      Array.prototype.forEach.call(headings, function(heading) {
        var headingText = heading.textContent.trim();
        var headingLevel = heading.tagName.toLowerCase();

        // Generate slug-based ID (or use existing if present)
        var headingId = heading.id;
        if (!headingId) {
          headingId = 'toc-' + headingText
            .toLowerCase()
            .replace(/[^a-z0-9]+/g, '-')
            .replace(/^-+|-+$/g, '')
            .substring(0, 50) + '-' + headingIndex;
          heading.id = headingId;
        }

        // Build TOC list item
        var liClass = headingLevel === 'h3' ? 'toc-h3' : '';
        tocHTML += '<li class="' + liClass + '">';
        tocHTML += '<a href="#' + headingId + '">' + headingText + '</a>';
        tocHTML += '</li>';

        headingIndex++;
      });

      // Create TOC container
      var tocContainer = document.createElement('nav');
      tocContainer.className = 'table-of-contents';
      tocContainer.setAttribute('role', 'navigation');
      tocContainer.setAttribute('aria-label', 'Table of Contents');

      tocContainer.innerHTML = 
        '<h4 onclick="toggleTOC(this)">' +
        'Table of Contents' +
        '<span class="toc-toggle-icon expanded">‚ñº</span>' +
        '</h4>' +
        '<ul id="toc-list">' + tocHTML + '</ul>';

      // Insert TOC before the first heading
      var firstHeading = headings[0];
      firstHeading.parentNode.insertBefore(tocContainer, firstHeading);

      console.log('[TOC] Successfully generated TOC with ' + headings.length + ' headings');

    } catch (error) {
      console.error('[TOC] Error generating table of contents:', error);
    }
  }

  // Toggle function (global scope for onclick)
  window.toggleTOC = function(headerElement) {
    var tocList = document.getElementById('toc-list');
    var toggleIcon = headerElement.querySelector('.toc-toggle-icon');

    if (tocList && toggleIcon) {
      if (tocList.style.display === 'none') {
        tocList.style.display = 'block';
        toggleIcon.classList.add('expanded');
        toggleIcon.textContent = '‚ñº';
      } else {
        tocList.style.display = 'none';
        toggleIcon.classList.remove('expanded');
        toggleIcon.textContent = '‚ñ∂';
      }
    }
  };

  // Smooth scroll enhancement (if not already set in CSS)
  document.addEventListener('click', function(e) {
    if (e.target.tagName === 'A' && e.target.getAttribute('href').startsWith('#toc-')) {
      e.preventDefault();
      var targetId = e.target.getAttribute('href').substring(1);
      var targetElement = document.getElementById(targetId);

      if (targetElement) {
        targetElement.scrollIntoView({
          behavior: 'smooth',
          block: 'start'
        });

        // Update URL without scrolling
        if (history.pushState) {
          history.pushState(null, null, '#' + targetId);
        }
      }
    }
  });

})();
