(function() {
  'use strict';

  // Create button after page load
  window.addEventListener('load', function() {
    // Create button element
    var btn = document.createElement('div');
    btn.id = 'goToBottom';
    btn.setAttribute('aria-label', 'Go to bottom');
    btn.setAttribute('title', 'Go to bottom');
    btn.innerHTML = '‚Üì';
    document.body.appendChild(btn);

    // Scroll handler with debounce for performance
    var scrollTimer;
    function handleScroll() {
      clearTimeout(scrollTimer);
      scrollTimer = setTimeout(function() {
        var scrolled = window.pageYOffset || document.documentElement.scrollTop;
        var windowHeight = window.innerHeight;
        var documentHeight = document.documentElement.scrollHeight;

        // Show button when scrolled down 300px and not at bottom
        if (scrolled > 300 && (scrolled + windowHeight < documentHeight - 100)) {
          btn.classList.add('show');
        } else {
          btn.classList.remove('show');
        }
      }, 100);
    }

    // Scroll to bottom function
    btn.addEventListener('click', function() {
      window.scrollTo({
        top: document.documentElement.scrollHeight,
        behavior: 'smooth'
      });
    });

    // Listen to scroll with passive for better performance
    window.addEventListener('scroll', handleScroll, { passive: true });

    // Initial check
    handleScroll();
  });
})();
