(function() {
  'use strict';

  // Mark images as loaded when they complete loading
  function handleImageLoading() {
    var lazyImages = document.querySelectorAll('img[loading="lazy"]');

    lazyImages.forEach(function(img) {
      if (img.complete) {
        img.classList.add('loaded');
      } else {
        img.addEventListener('load', function() {
          this.classList.add('loaded');
        });
        img.addEventListener('error', function() {
          this.classList.add('loaded');
        });
      }
    });
  }

  // Run on DOM ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', handleImageLoading);
  } else {
    handleImageLoading();
  }

  // Also handle dynamically loaded images
  if ('MutationObserver' in window) {
    var observer = new MutationObserver(function(mutations) {
      mutations.forEach(function(mutation) {
        mutation.addedNodes.forEach(function(node) {
          if (node.nodeType === 1) {
            if (node.tagName === 'IMG' && node.getAttribute('loading') === 'lazy') {
              if (node.complete) {
                node.classList.add('loaded');
              } else {
                node.addEventListener('load', function() {
                  this.classList.add('loaded');
                });
              }
            }
            var imgs = node.querySelectorAll && node.querySelectorAll('img[loading="lazy"]');
            if (imgs) {
              imgs.forEach(function(img) {
                if (img.complete) {
                  img.classList.add('loaded');
                } else {
                  img.addEventListener('load', function() {
                    this.classList.add('loaded');
                  });
                }
              });
            }
          }
        });
      });
    });

    observer.observe(document.body, { childList: true, subtree: true });
  }
})();
